import { PipedreamClient } from "../../src/Client";
import { mockServerPool } from "../mock-server/MockServerPool";
import { mockOAuth } from "./mockAuth";

describe("OauthTokensClient", () => {
    test("create", async () => {
        const server = mockServerPool.createServer();
        mockOAuth(server);

        const client = new PipedreamClient({
            maxRetries: 0,
            projectId: "projectId",
            clientId: "client_id",
            clientSecret: "client_secret",
            projectEnvironment: "test",
            environment: server.baseUrl,
        });
        const rawRequestBody = {
            grant_type: "client_credentials",
            client_id: "client_id",
            client_secret: "client_secret",
        };
        const rawResponseBody = { access_token: "access_token", token_type: "token_type", expires_in: 1 };
        server
            .mockEndpoint()
            .post("/v1/oauth/token")
            .jsonBody(rawRequestBody)
            .respondWith()
            .statusCode(200)
            .jsonBody(rawResponseBody)
            .build();

        const response = await client.oauthTokens.create({
            clientId: "client_id",
            clientSecret: "client_secret",
        });
        expect(response).toEqual({
            accessToken: "access_token",
            tokenType: "token_type",
            expiresIn: 1,
        });
    });
});
